    private void hitungBalok() {
        Label label1 = new Label("Masukkan panjang:");
        TextField panjangTextField = new TextField();
        panjangTextField.setMaxWidth(100);
        panjangTextField.setMaxHeight(40);
        Label label2 = new Label("Masukkan lebar:");
        TextField lebarTextField = new TextField();
        lebarTextField.setMaxWidth(100);
        lebarTextField.setMaxHeight(40);
        Label label3 = new Label("Masukkan tinggi:");
        TextField tinggiTextField = new TextField();
        tinggiTextField.setMaxWidth(100);
        tinggiTextField.setMaxHeight(40);
        Button hitungButton = new Button("Calculate");
        hitungButton.setBackground(background2);
        hitungButton.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

        Label hasilVolume = new Label();
        Label hasilLuas = new Label();

        hitungButton.setOnAction(action -> {
            try {
                Double panjang = Double.parseDouble(panjangTextField.getText());
                Double lebar = Double.parseDouble(lebarTextField.getText());
                Double tinggi = Double.parseDouble(tinggiTextField.getText());
                Double volume = panjang * lebar * tinggi;
                hasilVolume.setText("Volume balok: " + String.format("%.2f", volume));
                hasilVolume.setBackground(background);
                hasilVolume.setPrefWidth(150);
                hasilVolume.setPrefHeight(75);
                hasilVolume.setAlignment(Pos.CENTER);
                hasilVolume.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

                Double luas = 2 * (panjang * lebar + panjang * tinggi + lebar * tinggi);
                hasilLuas.setText("Luas Balok : " + String.format("%.2f", luas));
                hasilLuas.setBackground(background);
                hasilLuas.setPrefWidth(150);
                hasilLuas.setPrefHeight(75);
                hasilLuas.setAlignment(Pos.CENTER);
                hasilLuas.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");
            } catch (NumberFormatException e) {
                hasilVolume.setText("Masukkan angka yang valid");
                hasilLuas.setText("Masukkan angka yang valid");
            }
        });
        Button backhome = new Button("Back to Menu");
        backhome.setOnAction(action -> bangunRuangScene());

        VBox vbox = new VBox(10, label1, panjangTextField, label2, lebarTextField, label3, tinggiTextField,
                hitungButton, hasilVolume, hasilLuas, backhome);
        vbox.setAlignment(Pos.CENTER);
        vbox.setStyle("-fx-background-color: #00BFFF;");

        Scene scene = new Scene(vbox, 600, 600);
        stage.setScene(scene);
    }

    private void hitungTabung() {
        final double phi = 3.1415;
        Label label1 = new Label("Masukkan jari-jari:");
        TextField jariJariTextField = new TextField();
        jariJariTextField.setMaxWidth(100);
        jariJariTextField.setMaxHeight(40);
        Label label2 = new Label("Masukkan tinggi:");
        TextField tinggiTextField = new TextField();
        tinggiTextField.setMaxWidth(100);
        tinggiTextField.setMaxHeight(40);
        Button hitungButton = new Button("Calculate");
        hitungButton.setBackground(background2);
        hitungButton.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

        Label hasilVolume = new Label();
        Label hasilLuas = new Label();

        hitungButton.setOnAction(action -> {
            try {
                double jariJari = Double.parseDouble(jariJariTextField.getText());
                double tinggi = Double.parseDouble(tinggiTextField.getText());
                double volume = Math.PI * Math.pow(jariJari, 2) * tinggi;
                hasilVolume.setText("Volume tabung: " + String.format("%.2f", volume));
                hasilVolume.setBackground(background);
                hasilVolume.setPrefWidth(150);
                hasilVolume.setPrefHeight(75);
                hasilVolume.setAlignment(Pos.CENTER);
                hasilVolume.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

                double luas = 2 * phi * jariJari * (jariJari + tinggi);
                hasilLuas.setText("Hasil Luas : " + String.format("%.2f", luas));
                hasilLuas.setBackground(background);
                hasilLuas.setPrefWidth(150);
                hasilLuas.setPrefHeight(75);
                hasilLuas.setAlignment(Pos.CENTER);
                hasilLuas.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");
            } catch (NumberFormatException e) {
                hasilVolume.setText("Masukkan angka yang valid");
                hasilLuas.setText("Masukkan angka yang valid");
            }
        });
        Button backhome = new Button("Back to Menu");
        backhome.setOnAction(action -> bangunRuangScene());

        VBox vbox = new VBox(10, label1, jariJariTextField, label2, tinggiTextField, hitungButton, hasilVolume,
                hasilLuas, backhome);
        vbox.setAlignment(Pos.CENTER);
        vbox.setStyle("-fx-background-color: #00BFFF;");

        Scene scene = new Scene(vbox, 600, 600);
        stage.setScene(scene);
    }

    private void hitungBola() {
        final double phi = 3.1415;
        Label label = new Label("Masukkan jari-jari bola:");
        TextField jariJariTextField = new TextField();
        jariJariTextField.setMaxWidth(100);
        jariJariTextField.setMaxHeight(40);
        Button hitungButton = new Button("Calculate");
        hitungButton.setBackground(background2);
        hitungButton.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

        Label hasilVolume = new Label();
        Label hasilLuas = new Label();
        hitungButton.setOnAction(action -> {
            try {
                double jariJari = Double.parseDouble(jariJariTextField.getText());
                double volume = (4.0 / 3.0) * Math.PI * Math.pow(jariJari, 3);
                hasilVolume.setText("Volume bola: " + String.format("%.2f", volume));
                hasilVolume.setBackground(background);
                hasilVolume.setPrefWidth(150);
                hasilVolume.setPrefHeight(75);
                hasilVolume.setAlignment(Pos.CENTER);
                hasilVolume.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

                double luas = 4 * phi * jariJari * jariJari;
                hasilLuas.setText("Luas Bola : " + String.format("%.2f", luas));
                hasilLuas.setBackground(background);
                hasilLuas.setPrefWidth(150);
                hasilLuas.setPrefHeight(75);
                hasilLuas.setAlignment(Pos.CENTER);
                hasilLuas.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");
            } catch (NumberFormatException e) {
                hasilVolume.setText("Masukkan angka yang valid");
                hasilLuas.setText("Masukkan angka yang valid");
            }
        });
        Button backhome = new Button("Back to Menu");
        backhome.setOnAction(action -> bangunRuangScene());

        VBox vbox = new VBox(10, label, jariJariTextField, hitungButton, hasilVolume, hasilLuas, backhome);
        vbox.setAlignment(Pos.CENTER);
        vbox.setStyle("-fx-background-color: #00BFFF;");

        Scene scene = new Scene(vbox, 600, 600);
        stage.setScene(scene);
    }

