    private void hitungLayangLayang() {
        Label kelilingLayanglayang = new Label();
        Label luasLayanglayang = new Label();

        Label label1 = new Label("Masukkan diagonal 1:");
        TextField diagonal1TextField = new TextField();
        diagonal1TextField.setMaxWidth(100);
        diagonal1TextField.setMaxHeight(40);
        Label label2 = new Label("Masukkan diagonal 2:");
        TextField diagonal2TextField = new TextField();
        diagonal2TextField.setMaxWidth(100);
        diagonal2TextField.setMaxHeight(40);
        Button hitungButton = new Button("Calculate");
        hitungButton.setBackground(background2);
        hitungButton.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

        hitungButton.setOnAction(action -> {
            try {
                double diagonal1 = Double.parseDouble(diagonal1TextField.getText());
                double diagonal2 = Double.parseDouble(diagonal2TextField.getText());
                double keliling = 0.5 * diagonal1 * diagonal2;
                kelilingLayanglayang.setText("Keliling : " + String.format("%.2f", keliling));
                kelilingLayanglayang.setBackground(background);
                kelilingLayanglayang.setPrefWidth(150);
                kelilingLayanglayang.setPrefHeight(75);
                kelilingLayanglayang.setAlignment(Pos.CENTER);
                kelilingLayanglayang.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

                double luas = 0.5 * diagonal1 * diagonal2;
                luasLayanglayang.setText("Luas layang-layang: " + String.format("%.2f", luas));
                luasLayanglayang.setBackground(background);
                luasLayanglayang.setPrefWidth(150);
                luasLayanglayang.setPrefHeight(75);
                luasLayanglayang.setAlignment(Pos.CENTER);
                luasLayanglayang.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");
            } catch (NumberFormatException e) {
                kelilingLayanglayang.setText("Masukkan angka yang valid");
                luasLayanglayang.setText("Masukkan angka yang valid");
            }
        });
        Button backhome = new Button("Back to Menu");
        backhome.setOnAction(action -> bangunDatarScene());

        VBox vbox = new VBox(10, label1, diagonal1TextField, label2, diagonal2TextField, hitungButton,
                kelilingLayanglayang, luasLayanglayang, backhome);
        vbox.setAlignment(Pos.CENTER);
        vbox.setStyle("-fx-background-color: #00BFFF;");

        Scene scene = new Scene(vbox, 600, 600);
        stage.setScene(scene);
    }

    private void trigonometry() {
        Label gino = new Label("Trigonometry");
        gino.setStyle("-fx-font-size: 30px; -fx-text-fill: black;");

        VBox vbox3 = new VBox(10, gino);
        vbox3.setAlignment(Pos.CENTER);

        Label sinLabel = new Label();
        Label cosLabel = new Label();
        Label tanLabel = new Label();
        TextField inputTextField = new TextField();
        inputTextField.setMaxWidth(100);
        inputTextField.setMaxHeight(40);
        inputTextField.setPromptText("Masukkan Inputan");
        Button hitungbutton = new Button("Calculate");
        hitungbutton.setBackground(background2);
        hitungbutton.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

        hitungbutton.setOnAction(action -> {
            try {
                double angle = Double.parseDouble(inputTextField.getText());
                double radians = Math.toRadians(angle);

                double sin = Math.sin(radians);
                double cos = Math.cos(radians);
                double tan = Math.tan(radians);

                sinLabel.setText("Sinus   : " + String.format("%.2f", sin));
                sinLabel.setBackground(background);
                sinLabel.setPrefWidth(150);
                sinLabel.setPrefHeight(75);
                sinLabel.setAlignment(Pos.CENTER);
                sinLabel.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");
                cosLabel.setText("Kosinus : " + String.format("%.2f", cos));
                cosLabel.setBackground(background);
                cosLabel.setPrefWidth(150);
                cosLabel.setPrefHeight(75);
                cosLabel.setAlignment(Pos.CENTER);
                cosLabel.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");
                tanLabel.setText("Tangen  : " + String.format("%.2f", tan));
                tanLabel.setBackground(background);
                tanLabel.setPrefWidth(150);
                tanLabel.setPrefHeight(75);
                tanLabel.setAlignment(Pos.CENTER);
                tanLabel.setStyle("-fx-font-size: 15px; -fx-text-fill: white;");

            } catch (NumberFormatException e) {
                sinLabel.setText("Masukkan angka yang valid");
                cosLabel.setText("Masukkan angka yang valid");
                tanLabel.setText("Masukkan angka yang valid");
            }
        });
        Button backhome = new Button("Back to Menu");
        backhome.setOnAction(action -> secondScene());

        VBox vbox = new VBox(10, gino, inputTextField, hitungbutton, sinLabel, cosLabel, tanLabel, backhome);
        vbox.setAlignment(Pos.CENTER);
        vbox.setStyle("-fx-background-color: #00BFFF;");

        Scene scene3 = new Scene(vbox, 600, 600);
        stage.setScene(scene3);
    }

 
